import { EsMappingService } from './es-mapping.service';

/**
 * Argument for an elasticsearch index
 */
export class EsEntityArgs {
  /** Name of the index */
  index: string;
  /** Type of the index */
  type?: string;
  /** create mapping or not **/
  readonly?: boolean;
}

/**
 * @EsEntity decorator : registrer the entity in the mapping through the EsMappingService
 * @param args decorator annotation
 */
export function EsEntity(args?: EsEntityArgs): ClassDecorator {
  return function (target: any) {
    EsMappingService.getInstance().addEntity(args, target);
  };
}

import { EsMappingService } from './es-mapping.service';
import 'reflect-metadata';

/**
 * Argument for a simple elasticsearch field
 */
export class EsFieldArgs {
  /** Type of the field : "text" | "integer" | */
  type: string;
  /** Name of the field : if it need to be different of the property name*/
  name?: string;
  /** Additional properties or not */
  dynamic?: boolean;
  /** Analyzer type */
  analyzer?: string;
  /** Additionnal ES fields **/
  fields?: any;
  /** Format */
  format?: any;
  /** Enabled */
  enabled?: boolean;
  /** Define the null value */
  null_value?: any;
  /** copy into a group field */
  copy_to?: string;
  /** Relations for join datatype */
  relations?: any;
  /** Define class for "array/nested" type */
  fieldClass?: any;
  /** Additional properties */
  [x: string]: any;
}

/**
 * @EsField decorator : registrer the field in the mapping through the EsMappingService
 * @param args decorator annotation
 */
export function EsField(args: EsFieldArgs): PropertyDecorator {
  return function (target: any, propertyKey: string | symbol) {

    let propertyType = Reflect.getMetadata('design:type', target, propertyKey);
    if (args.type === 'join' && !args.relations) {
      throw new Error(`es-mapping-error no relations defined for join datatype : ${target.constructor.name}:${<string>propertyKey}`);
    }

    if (args.type === 'nested') {
      if (propertyType.name !== 'Array' && !propertyType.values) {
        throw new Error(`es-mapping-error type of a nested field must be an array : ${target.constructor.name}:${<string>propertyKey}`);
      }
      if (!args.fieldClass) {
        console.warn(`es-mapping-warning no fieldClass defined for nested datatype : ${target.constructor.name}:${<string>propertyKey}`);
      }
    }

    if (args.type === 'object' && !args.fieldClass) {
      if (propertyType.name === 'Array') {
        console.warn(`es-mapping-warning no fieldClass defined for object array datatype :
        ${target.constructor.name}:${<string>propertyKey}`);
      }
    }

    if (args.fieldClass) {
      propertyType = args.fieldClass;
    }

    EsMappingService.getInstance().addField(args, target, propertyKey, propertyType);
  };
}

import { EsMapping, EsMappingProperty, InternalEsMapping, InternalEsMappingProperty } from './es-mapping';
import { EsFieldArgs } from './es-field.decorator';
import { EsEntityArgs } from './es-entity.decorator';
import * as lodash from 'lodash';
import * as bluebird from 'bluebird';
import { Client } from 'elasticsearch';

/**
 * Service used to manage mapping loading and share it
 */
export class EsMappingService {

  static instance: EsMappingService;

  esMappings: Map<String, InternalEsMapping> = new Map();

  constructor() { }

  /**
   * Get the singleton instance
   */
  static getInstance(): EsMappingService {
    if (!EsMappingService.instance) {
      const esMappingService = new EsMappingService();
      EsMappingService.instance = esMappingService;
    }
    return EsMappingService.instance;
  }

  /**
   * Add the entity in the mapping
   * @param args decorator args
   * @param target class
   */
  addEntity(args: EsEntityArgs, target: any): void {
    const className = target.name;
    let mapping = this.esMappings.get(className);
    if (!mapping) {
      mapping = new InternalEsMapping();
      this.esMappings.set(className, mapping);
    }
    if (args) {
      mapping.esmapping.index = args.index;
      mapping.esmapping.type = args.type;
      mapping.readonly = (args.readonly === true);
    }
    mapping.mergeEsMapping();
  }

  /**
   * Add the field in the mapping
   * @param args decorator args
   * @param target class
   * @param propertyKey the property
   */
  addField(args: EsFieldArgs, target: any, propertyKey: string | symbol, propertyType?: any): void {
    const className = target.constructor.name;
    let mapping = this.esMappings.get(className);
    if (!mapping) {
      mapping = new InternalEsMapping();
      this.esMappings.set(className, mapping);
      mapping.mergeEsMapping();
    }

    let properties: EsMappingProperty = {};
    if (args) {
      if (args.type === 'nested' || args.type === 'object') {
        properties.type = args.type;
        const esEntity = this.esMappings.get(propertyType.name);
        if (esEntity) {
          properties.properties = esEntity.esmapping.body.properties;
        }
      } else {
        properties = args;
      }

      const internalProperty: InternalEsMappingProperty = {
        propertyMapping: properties
      };

      const propertyName = args.name || propertyKey;
      mapping.addProperty(propertyName, internalProperty);
    } else {
      const internalProperty: InternalEsMappingProperty = {
        propertyMapping: {}
      };
      mapping.addProperty(propertyKey, internalProperty);
    }
  }

  /**
   * Alllow you to get the generated mapping list ready to be inserted inside elasticsearch
   */
  public getMappings(): Array<InternalEsMapping> {
    return Array.from(this.esMappings.values());
  }

  /**
   * Allow you to get all index
   */
  public getEsMappings(): Array<EsMapping> {
    return lodash.map(Array.from(this.esMappings.values()), (mapping) => {
      return mapping.esmapping;
    });
  }

  /**
   * Allow you to get the generate mapping map
   */
  public getMappingsMap() {
    return this.esMappings;
  }

  /**
   * Alllow you to get the generated mapping ready to be inserted inside elasticsearch
   * for a class name
   */
  public getMappingForClass(className: String): EsMapping {
    const internalMapping = this.esMappings.get(className);

    if (internalMapping) {
      return internalMapping.esmapping;
    }
    return null;
  }

  /**
   * Alllow you to get the generated mapping  ready to be inserted inside elasticsearch
   * for an index name
   */
  public getMappingForIndex(indexName: String): EsMapping {
    const internalMapping = lodash.find(Array.from(this.esMappings.values()), (internalEsMapping) => {
      return internalEsMapping.esmapping.index === indexName;
    });

    if (internalMapping) {
      return internalMapping.esmapping;
    }
    return null;
  }

  /**
 * Alllow you to get the generated mapping  eady to be inserted inside elasticsearch
 * for an type
 */
  public getMappingForType(type: String): EsMapping {
    const internalMapping = lodash.find(Array.from(this.esMappings.values()), (internalEsMapping) => {
      return internalEsMapping.esmapping.type === type;
    });

    if (internalMapping) {
      return internalMapping.esmapping;
    }
    return null;
  }

  /**
   * Allow you to get all index
   */
  public getAllIndex(): Array<String> {
    return lodash.map(Array.from(this.esMappings.values()), (mapping) => {
      return mapping.esmapping.index;
    });
  }

  /**
   * Allow to insert/update mapping into elasticsearch
   */
  public async uploadMappings(esclient: Client) {
    const mappings = EsMappingService.getInstance().getMappings();

    await bluebird.each(mappings, async (internalMapping) => {
      if (!internalMapping.readonly) {
        const esMapping = internalMapping.esmapping;

        if (esMapping.index) {
          // Delete readonly for ES compatibility
          delete internalMapping.readonly;

          const indexExist = await esclient.indices.exists({ index: esMapping.index });
          if (!indexExist) {
            // create index
            await esclient.indices.create({ index: esMapping.index });
            // create mapping
            await esclient.indices.putMapping(esMapping);
          } else {
            // update mapping
            await esclient.indices.putMapping(esMapping);
          }
        }
      }
    });
  }
}

/**
 * Base format of an elasticsearch mapping
 */
export class EsMapping {
  index: string;
  type: string;
  body: { properties: any };

  constructor() {
    this.body = { properties: {} };
  }
}

/**
 * Internal mapping to handle specific parameter
 */
export class InternalEsMapping {
  index: string;
  type: string;
  readonly: boolean;
  esmapping: EsMapping;
  properties: Map<string | symbol, InternalEsMappingProperty> = new Map();

  constructor() {
    this.esmapping = new EsMapping();
  }

  mergeEsMapping(): void {
    if (!this.esmapping) {
      this.esmapping = new EsMapping();
    }
    this.esmapping.index = this.esmapping.index;
    this.esmapping.type = this.esmapping.type;
  }

  addProperty(name: string | symbol, mapping: InternalEsMappingProperty): void {
    this.properties.set(name, mapping);
    this.esmapping.body.properties[name] = mapping.propertyMapping;
  }
}

/**
 * Base format of an elasticsearch property
 */
export interface EsMappingProperty {
  type?: string;
  analyzer?: string;
  properties?: any;
  fields?: any;
}

/**
 * Base format of an elasticsearch property
 */
export interface InternalEsMappingProperty extends EsMappingProperty {
  propertyMapping: EsMappingProperty;
  transformers?: EsMappingPropertyTranformer[];
}

export interface EsMappingPropertyTranformer {
  fieldName: string;
  transformer: EsPropertyTranformer;
}

export interface EsPropertyTranformer {
  transform(input: any);
}

export * from './es-entity.decorator';
export * from './es-field.decorator';
export * from './es-mapping.service';
export * from './es-mapping';

import 'reflect-metadata';
import './resources/master.entity';
import { EsMappingService } from '../lib/es-mapping-ts';

describe('es-mapping-test', () => {

  it('Mapping for type "masterType" should exist', () => {
    const mappings = EsMappingService.getInstance().getMappingForType('masterType');
    expect(mappings).toBeDefined();
  });

  it('Mapping for index "master" should exist', () => {
    const mappings = EsMappingService.getInstance().getMappingForIndex('master');
    expect(mappings).toBeDefined();
  });

  it('Mapping for index "nested" should exist', () => {
    const mappings = EsMappingService.getInstance().getMappingForIndex('nested');
    expect(mappings).toBeDefined();
  });

  it('Mapping for index "object" should not exist', () => {
    const mappings = EsMappingService.getInstance().getMappingForIndex('object');
    expect(mappings).toBeNull();
  });

  it('Mapping for class "ObjectEntity" should exist', () => {
    const mappings = EsMappingService.getInstance().getMappingForClass('ObjectEntity');
    expect(mappings).toBeDefined();
  });

  it('Mapping for type "masterType" should exist', () => {
    const mapping = EsMappingService.getInstance().getMappingForIndex('master');
    expect(mapping).toBeDefined();
    expect(mapping.body).toBeDefined();
    expect(mapping.index).toEqual('master');
    expect(mapping.type).toEqual('masterType');
    expect(mapping.body.properties.name).toBeDefined();

    expect(mapping.body.properties.firstname).toBeDefined();
    expect(mapping.body.properties.lastname).toBeDefined();
    expect(mapping.body.properties.master).toBeDefined();
    expect(mapping.body.properties.objects).toBeDefined();
    expect(mapping.body.properties.objects.type).toEqual('object');
    expect(mapping.body.properties.objects.properties).toBeDefined();

    expect(mapping.body.properties.nesteds).toBeDefined();
    expect(mapping.body.properties.nesteds.type).toEqual('nested');
    expect(mapping.body.properties.nesteds.properties).toBeDefined();
  });

  it('should not load entity with non array nested field', async () => {
    try {
      require('./resources/wrong-nested.entity');
    } catch (err) {
      expect(err).toBeDefined();
      expect(err.message).toEqual('es-mapping-error type of a nested field must be an array : WrongNestedEntity:nesteds');
    }
  });

  it('should not load entity with join relation without relations defined', async () => {
    try {
      require('./resources/wrong-relation.entity');
    } catch (err) {
      expect(err).toBeDefined();
      expect(err.message).toEqual('es-mapping-error no relations defined for join datatype : WrongRelationEntity:relations');
    }
  });
});

import { EsEntity, EsField } from '../../lib/es-mapping-ts';
import { ObjectEntity } from './object.entity';
import { NestedEntity } from './nested.entity';

@EsEntity({
  index: 'master',
  type: 'masterType'
})
export class MasterEntity {

  @EsField({
    type : 'text'
  })
  name?: string;

  @EsField({
    type: 'text',
    copy_to : 'name'
  })
  firstname: string;

  @EsField({
    type: 'text',
    copy_to : 'name'
  })
  lastname: string;

  @EsField({
    type: 'join',
    relations: { 'master': 'submaster' }
  })
  master: Array<MasterEntity>;

  @EsField({
    type: 'object',
    fieldClass: ObjectEntity
  })
  objects: Array<MasterEntity>;

  @EsField({
    type: 'nested',
    fieldClass: NestedEntity
  })
  nesteds: Array<NestedEntity>;
}

import { EsEntity, EsField } from '../../lib/es-mapping-ts';

@EsEntity({
  index: 'nested'
})
export class NestedEntity {

  @EsField({
    type: 'text',
  })
  name: string;

  @EsField({
    type: 'integer'
  })
  montant: number;
}

import { EsEntity, EsField } from '../../lib/es-mapping-ts';

@EsEntity()
export class ObjectEntity {

  @EsField({
    type: 'text',
    analyzer : 'whitespace'
  })
  name: string;

  @EsField({
    type: 'integer',
  })
  age: number;
}

import { EsEntity, EsField } from '../../lib/es-mapping-ts';
import { NestedEntity } from './nested.entity';

@EsEntity()
export class WrongNestedEntity {

  @EsField({
    type: 'nested'
  })
  nesteds: NestedEntity;
}

import { EsEntity, EsField } from '../../lib/es-mapping-ts';

@EsEntity()
export class WrongRelationEntity {

  @EsField({
    type: 'join'
  })
  relations: WrongRelationEntity;
}
